import React from 'react'; 

import {
  GroupContainer,
  FormInputContainer,
  FormInputLabel
} from './form-input.styles';

const FormInput = ({ handleChange, label, ...props }) => (
    <GroupContainer>
        <input className='form-input' onChange={handleChange} {...props} />
        {
            label ? (
                <label 
                className={`${props.value.length ? 'shrink' : ''} 
                form-input-label `}
                >
                    {label}
                </label>
                )
            : null
        }
    </GroupContainer>
)

export default FormInput;
