import React from "react";

import {
	CartItemContainer,
	CartItemImage,
	ItemDetailsContainer,
} from "./cart-item.styles";
import "./cart-item.styles.scss";

const CartItem = ({ item: { name, imageUrl, price, quantity } }) => (
	<CartItemContainer>
		<CartItemImage src={imageUrl} alt="item" />
		<ItemDetailsContainer>
			<span>{name}</span>
			<span>
				{quantity} x ${price}
			</span>
		</ItemDetailsContainer>
	</CartItemContainer>
);

export default CartItem;
